import React, {Component} from 'react'
import {Container, Row, Col, Table, Button, Form,Image} from 'react-bootstrap'
import Header from '../components/header.js'
import fetch from 'isomorphic-unfetch'

class Records extends Component {

  static async getInitialProps({req}){
    // const id = req.user.User_id
    var res = await fetch(`https://9d170bae.ngrok.io/Transactions/Mixed/14765d72-67c6-11e9-b73a-59c7d9ed16a4/0/secret`)

    const json = await res.json()

    res = await fetch(`https://9d170bae.ngrok.io/Accounts/ids/14765d72-67c6-11e9-b73a-59c7d9ed16a4/secret`)

    const accountsJson = await res.json()
    console.log("accountjson")
    console.log(accountsJson)

    return {transactions: json, ids: accountsJson}
  }

  constructor(props){
    super(props)

    this.state = {
      selectAccount: "All",
      transactions: this.props.transactions,
      filteredTransactions: this.props.transactions
    }

    this.handleSelection = this.handleSelection.bind(this)
  }

  handleSelection(event){
    let id = event.target.value
    var transactions;
    var isValid = function(props){
    var ids = props.transactions.map((account) => account.Account_id)
    return ids.includes(id)
    }

    if (id != "All") {
      transactions = this.state.transactions.filter((transaction) =>
      (isValid(this.state) ? transaction.sender_account_id : transaction.receiver_account_id) == id
    )
    } else {
      transactions  = this.state.transactions
    }
    this.setState({
      selectAccount: id,
      filteredTransactions: transactions
    })
  }

  render(){
    return(
      <Container>
      <Header />
        <Row>
          <Col>
            <FilterRecords accounts={this.props.ids} parent={this} />
          </Col>
          <Col>
            <TransactionsTable parent={this} ids={this.props.ids} />
          </Col>
        </Row>
      </Container>
    )
  }
}

class FilterRecords extends Component {
  render(){
    return(
      <Form>
        <Form.Group>
          <Form.Control as ="select" name="selectAccount" value={this.props.parent.state.selectAccount} onChange={this.props.parent.handleSelection}>
            <option value="All">All Accounts</option>
            {this.props.accounts.map((account) =>
                <option value={account.Account_id}>{account.Type_name + " - " + account.Account_id}</option>
            ) }
          </Form.Control>
        </Form.Group>
      </Form>
    )
  }
}

class TableHeader extends Component {
  render(){
    return(
      <th>
        <Button>
        <p>{this.props.title}</p>
        <Image />
        </Button>
      </th>
    )
  }
}

class TableRow extends Component {
  //amount, sender_account_id, receiver_account_id, date_time,sender_name,receiver_name

  render(){
    var isValid = function(props){
    var ids = props.ids.map((account) => account.Account_id)
    return ids.includes(props.transaction.sender_account_id)
    }
    return(
      <tr>
        <td>{this.props.transaction.date_time} </td>
        <td>${this.props.transaction.amount} </td>
        <td>{isValid(this.props) ?
          this.props.transaction.sender_account_id :
          this.props.transaction.receiver_account_id } </td>
        <td>{this.props.transaction.sender_name} </td>
        <td>{this.props.transaction.receiver_name} </td>
      </tr>
    )
  }
}

class TransactionsTable extends Component {

  render(){
    return(
      <Table striped bordered hover >
        <thead>
          <tr>
            <TableHeader title="Date" />
            <TableHeader title="Amount" />
            <TableHeader title="Account" />
            <TableHeader title="Sender" />
            <TableHeader title="Receiver" />
          </tr>
        </thead>
        <tbody>
          {this.props.parent.state.filteredTransactions.map((transaction) => <TableRow transaction={transaction} ids={this.props.ids} />)}
        </tbody>
      </Table>
    )
  }
}
//    '/Transactions/Mixed/:User_id/:rowAmount/:api_key'
//    '/Accounts/ids/:User_id/:api_key'
export default Records
