import React, { Component } from 'react';
import {Row, Col, ButtonToolbar, Button} from 'react-bootstrap';
import AccountHeader from './accountheader.js';

class AccountCard extends Component {
  render(){
    return(
      <Row>
        <Col>
          <AccountHeader type={this.props.account.Type_name} number="8929"/>
          <AccountBalance amount={this.props.account.Balance} />
          <AccountRecords id={this.props.account.Account_id} transactions={this.props.transactions} />
          <AccountActions />
        </Col>
      </Row>
    )
  }
}


class AccountBalance extends Component {
  render(){
    return(
      <Row>
        <Col>
          <p>${this.props.amount}</p>
        </Col>
      </Row>
    )
  }
}

class AccountRecords extends Component {
  /*

  { sender_account_id: 'f4f72ace-67bd-11e9-b73a-59c7d9ed16a4',
        receiver_account_id: '1477077c-67c6-11e9-b73a-59c7d9ed16a4',
        date_time: '2019-04-27T22:01:51.000Z',
        sender_name: 'Alejandro Lara',
        receiver_name: 'Michael Fulton' }

        */
  createValue(transaction){
    console.log(transaction)
    console.log(this.props.id == transaction.reciever_account_id ? transaction.sender_name : transaction.reciever_name )
    return {
      time: transaction.date_time,
      recipient:  (this.props.id == transaction.reciever_account_id ? transaction.sender_name : transaction.reciever_name) ,
      amount: transaction.amount
    }
  }
  render(){
    // const account = this.props.
    var value;
    console.log(this.props)
    var records = this.props.transactions.map((transaction) =>
    <Record value = {this.createValue(transaction)} />
  )

    return(
      <Row>
        <Col>
        {records}
        </Col>
      </Row>
    )
  }
}

class AccountActions extends Component {
  render(){
    return(
      <Row>
        <Col>
          <ButtonToolbar>
          <Button variant="outline-primary">RECORDS</Button>
          <Button variant="outline-primary">TRANSFER</Button>
          <Button variant="outline-primary">PAY BILLS</Button>
          </ButtonToolbar>
        </Col>
      </Row>
    )
  }
}

class Record extends Component {
  render(){
    return(
      <Row>
        <RecordTime value={this.props.value.time}/>
        <RecordRecipient value={this.props.value.recipient}/>
        <RecordAmount value={this.props.value.amount}/>
      </Row>
    )
  }
}

class RecordTime extends Component {
  render(){
    return(
      <Col>
        <p>{this.props.value}</p>
      </Col>
    )
  }
}

class RecordRecipient extends Component {
  render(){
    return(
      <Col>
        <p>{this.props.value}</p>
      </Col>
    )
  }
}

class RecordAmount extends Component {
  render(){
    return(
      <Col>
        <p>${this.props.value}</p>
      </Col>
    )
  }
}



export default AccountCard
