import React, {Component} from 'react'
import {Container, Row, Col, Table, Button, Form,Image} from 'react-bootstrap'
import Header from '../components/header.js'
import fetch from 'isomorphic-unfetch'

class Records extends Component {

  static async getInitialProps({req}){
    // const id = req.user.User_id
    var res = await fetch(`https://9d170bae.ngrok.io/Transactions/Mixed/14765d72-67c6-11e9-b73a-59c7d9ed16a4/0/secret`)

    const json = await res.json()

    res = await fetch(`https://9d170bae.ngrok.io/Accounts/ids/14765d72-67c6-11e9-b73a-59c7d9ed16a4/secret`)

    const accountsJson = await res.json()
    console.log("accountjson")
    console.log(accountsJson)

    return {transactions: json, ids: accountsJson}
  }

  constructor(props){
    super(props)

    this.state = {
      accounts: [],
      senders: [],
      receivers: [],
      filteredTransactions: props.transactions,
      transactions: props.transactions,
      ids: this.props.ids,
      Account: true,
      Senders: true,
      Receivers: true
    }

    this.filterTransactions = this.filterTransactions.bind(this)

  }

filterTransactions(event){
  let name = event.target.name
  let value = event.target.value
  let checked = event.target.checked
  console.log(this.state)
  switch(name) {
    case "Account":
      var values = this.state.accounts
      if (checked) {
        values.push(value)
      } else {
        values = values.filter((current) => !(current == value))
      }
      this.setState({
        accounts: values,
        [name]: !checked
      })
    break;
    case "Receiver":
    var values = this.state.receivers
    if (checked) {
      values.push(value)
    } else {
      values = values.filter((current) => !(current == value))
    }
    values.push(value)
    this.setState({
      receivers: values,
      [name]: !checked
    })
      break
    case "Sender":
    var values = this.state.senders
    if (checked) {
      values.push(value)
    } else {
      values = values.filter((current) => !(current == value))
    }
    values.push(value)
    this.setState({
      senders: values,
      [name]: !checked
    })
      break
    default:
      break
  }
console.log("Im here")

  var statevar = this.state
  var isValid = function(transaction){
  var ids = statevar.ids.map((account) => account.Account_id)
  return ids.includes(transaction.sender_account_id)
  }
  var filteredTransactions = this.state.transactions.filter(function(transaction){
    var condition;
    var account = isValid(transaction) ?
    transaction.sender_account_id :
      transaction.receiver_account_id

    return statevar.accounts.includes(account) &&
    statevar.receivers.includes(transaction.receiver_account_id)  &&
    statevar.senders.includes(transaction.sender_account_id)
  })

  this.setState({
    filteredTransactions: filteredTransactions
  })

}

  render(){
    return(
      <Container>
      <Header />
        <Row>
          <Col>
            <RecordsFilter selectedAccount={this.props.id} ids={this.state.ids} parent={this} transactions={this.state.transactions} />
          </Col>
          <Col>
            <TransactionsTable transactions={this.state.filteredTransactions} parent={this} ids={this.state.ids} />
          </Col>
        </Row>
      </Container>
    )
  }
}

class RecordsFilter extends Component {

  render(){
      var temp = []
      var condition;
      let senderList = this.props.transactions.filter(function(transaction){

          if (!temp.includes(transaction.sender_account_id)){

            temp.push(transaction.sender_account_id)
            return true
          }
          return false
      })
      temp = []
      let receiverList = this.props.transactions.filter(function(transaction){

          if (!temp.includes(transaction.receiver_account_id)){
            console.log("filter")
console.log(temp)
            temp.push(transaction.receiver_account_id)
            return true
          }
          return false
      })
    return(
      <Row>
        <Col>
        <p>Filter By:</p>
        <Form>
        <Form.Group>
          <Form.Label>Select an Account:</Form.Label>
          {
            this.props.ids.map((account)=>
            <Form.Check
            checked={this.props.parent.state.Account}
            type="checkbox"
            name="Account"
            onChange={this.props.parent.filterTransactions}
            value={account.Account_id}
            label={account.Type_name + " - " + account.Account_id} />)
          }
        </Form.Group>
        <Form.Group>
          <Form.Label>Sender:</Form.Label>
          {
            senderList.map((transaction) =>
            <Form.Check
            checked={this.props.parent.state.Sender}
            type="checkbox"
            name="Sender"
            onChange={this.props.parent.filterTransactions}
            value={transaction.sender_account_id}
            label={transaction.sender_name + " - " + transaction.sender_account_id} />)
          }
        </Form.Group>
        <Form.Group>
          <Form.Label>Reciever:</Form.Label>
          {
            receiverList.map((transaction) =>
            <Form.Check
            checked={this.props.parent.state.Receiver}
            type="checkbox"
            name="Receiver"
            onChange={this.props.parent.filterTransactions}
            value={transaction.receiver_account_id}
            label={transaction.receiver_name + " - " + transaction.receiver_account_id} />)
          }
        </Form.Group>

        </Form>
        </Col>
      </Row>
    )
  }
}

class TableHeader extends Component {
  render(){
    return(
      <th>
        <Button>
        <p>{this.props.title}</p>
        <Image />
        </Button>
      </th>
    )
  }
}

class TableRow extends Component {
  //amount, sender_account_id, receiver_account_id, date_time,sender_name,receiver_name

  render(){
    console.log("idsss")
    console.log(this.props.ids)
    var isValid = function(props){
    var ids = props.ids.map((account) => account.Account_id)
    console.log(ids)
    return ids.includes(props.transaction.sender_account_id)
    }
    return(
      <tr>
        <td>{this.props.transaction.date_time} </td>
        <td>${this.props.transaction.amount} </td>
        <td>{isValid(this.props) ?
          this.props.transaction.sender_account_id :
          this.props.transaction.receiver_account_id } </td>
        <td>{this.props.transaction.sender_name} </td>
        <td>{this.props.transaction.receiver_name} </td>
      </tr>
    )
  }
}

class TransactionsTable extends Component {
  constructor(props){
    super(props)

    this.state = {
      data: this.props.parent.state.filteredTransactions.map((transaction) => <TableRow transaction={transaction} ids={this.props.ids} />)
    }
  }

  render(){
    return(
      <Table striped bordered hover >
        <thead>
          <tr>
            <TableHeader title="Date" />
            <TableHeader title="Amount" />
            <TableHeader title="Account" />
            <TableHeader title="Sender" />
            <TableHeader title="Receiver" />
          </tr>
        </thead>
        <tbody>
          {this.state.data}
        </tbody>
      </Table>
    )
  }
}
//    '/Transactions/Mixed/:User_id/:rowAmount/:api_key'
//    '/Accounts/ids/:User_id/:api_key'
export default Records
