import React, { Component } from 'react';
import {Row, Col, ButtonToolbar, Button} from 'react-bootstrap';
import AccountHeader from './accountheader.js';

class AccountCard extends Component {
  render(){
    return(
      <Row>
        <Col>
          <AccountHeader type={this.props.value.Type_name} number="8929"/>
          <AccountBalance amount={this.props.value.Balance} />
          <AccountRecords  />
          <AccountActions />
        </Col>
      </Row>
    )
  }
}
class AccountBalance extends Component {
  render(){
    return(
      <Row>
        <Col>
          <p>${this.props.amount}</p>
        </Col>
      </Row>
    )
  }
}

class AccountRecords extends Component {
  render(){
    // const account = this.props.
    var value = {
      time: "12:13pm",
      recipient: "Michael Fulton",
      amount: "45.12"
    }
    return(
      <Row>
        <Col>
          <Record value={value} />
        </Col>
      </Row>
    )
  }
}

class AccountActions extends Component {
  render(){
    return(
      <Row>
        <Col>
          <ButtonToolbar>
          <Button variant="outline-primary">RECORDS</Button>
          <Button variant="outline-primary">TRANSFER</Button>
          <Button variant="outline-primary">PAY BILLS</Button>
          </ButtonToolbar>
        </Col>
      </Row>
    )
  }
}

class Record extends Component {
  render(){
    return(
      <Row>
        <RecordTime value={this.props.value.time}/>
        <RecordRecipient value={this.props.value.recipient}/>
        <RecordAmount value={this.props.value.amount}/>
      </Row>
    )
  }
}

class RecordTime extends Component {
  render(){
    return(
      <Col>
        <p>{this.props.value}</p>
      </Col>
    )
  }
}

class RecordRecipient extends Component {
  render(){
    return(
      <Col>
        <p>{this.props.value}</p>
      </Col>
    )
  }
}

class RecordAmount extends Component {
  render(){
    return(
      <Col>
        <p>${this.props.value}</p>
      </Col>
    )
  }
}



export default AccountCard
