import React, { Component } from 'react';
import {Container, Row, Col, Button, Form} from 'react-bootstrap';
import {Router} from 'next/router';


class SignUp extends Component {
  constructor(props){
    super(props);
    this.updateValues = this.updateValues.bind(this)
    this.submitValues = this.submitValues.bind(this)
    this.state = {
      index: 0,
      forms: [],
      user: {}
    }
    var user = {
      firstName: this.state.user.firstName,
    lastName : this.state.user.lastName,
    address: this.state.user.address,
    phone: this.state.user.phone,
    ssn: this.state.user.ssn
    }

        this.state.forms = [<AccountSelectionForm grandParent={this.props.parent} parent={this} accountType={this.state.user.accountType} onChange={this.updateValues} />,
            <PersonalInfoForm parent={this} onChange={this.updateValues} user={user} />, <LoginCredentialsForm email={this.state.user.email} password={this.state.user.password} parent={this}  submitValues={this.submitValues}/>]
              this.props.router
  }

  submitValues(e){

    console.log("hello")
    console.log(event)

  }


  updateValues(event){
    var name = event.target.name
    var value = event.target.value
    this.setState({user: {
      [name]: value
    }})
  }
  render(){


    return(
      <Row>
        <Col>
          {this.state.forms[this.state.index]}
        </Col>
      </Row>
    )
  }
}



class LoginCredentialsForm extends Component {
  constructor(props){
    super(props)
    this.goBack = this.goBack.bind(this)
  }
  goBack(){
    this.props.parent.setState({
      index: 1
    })
  }



  render(){
    return(
      <Row>
        <Col>
        <Form action='/login/true/something' method="post" onSubmit={this.props.submitValues}>
        <Form.Group controlId="formGroupEmail">
  <Form.Label>Email address</Form.Label>
  <Form.Control type="email" name="email" value={this.props.email} onChange={this.props.onChange} placeholder="" />
</Form.Group>
<Form.Group controlId="formGroupPassword">
  <Form.Label>Password</Form.Label>
  <Form.Control type="password" name="password" value={this.props.password} onChange={this.props.onChange} placeholder="" />
</Form.Group>
<Button variant="outline-danger" onClick={this.goBack} >Back</Button>

  <Button variant="outline-dark" type="submit">SUBMIT</Button>
        </Form>
        </Col>
      </Row>

    )
  }

}

class PersonalInfoForm extends Component {
  constructor(props){
    super(props)
    this.goBack = this.goBack.bind(this)
    this.next = this.next.bind(this)
  }
  goBack(){
    this.props.parent.setState({
      index: 0
    })
  }
  next(){
    this.props.parent.setState({
      index: 2
    })
  }

  render(){
    return(
      <Row>
        <Col>
          <Form>
          <Form.Group controlId="formGridFirstName">
            <Form.Label>First Name</Form.Label>
            <Form.Control name="firstName" value={this.props.user.firstName} onChange={this.props.onChange} placeholder="" />
          </Form.Group>
          <Form.Group controlId="formGridLastName">
            <Form.Label>Last Name</Form.Label>
            <Form.Control name="lastName" value={this.props.user.lastName} onChange={this.props.onChange} placeholder="" />
          </Form.Group>
          <Form.Group controlId="formGridAddress" >
            <Form.Label>Address</Form.Label>
            <Form.Control name="address" value={this.props.user.address} onChange={this.props.onChange} placeholder="" />
          </Form.Group>
          <Form.Group>
            <Form.Label controlId="formGridPhoneNumber">Phone Number</Form.Label>
            <Form.Control name="phone" value={this.props.user.phone} onChange={this.props.onChange} placeholder="" />
          </Form.Group>
          <Form.Group>
            <Form.Label controlId="formGridSSN">SSN</Form.Label>
            <Form.Control name="ssn" value={this.props.user.ssn} onChange={this.props.onChange} placeholder="" />
          </Form.Group>
          <Button variant="outline-danger" onClick={this.goBack} >Back</Button>

          <Button variant="outline-dark" onClick={this.next} >NEXT</Button>
          </Form>
        </Col>
      </Row>

    )
  }
}



class AccountSelectionForm extends Component {
  constructor(props){
    super(props)
    this.goBack = this.goBack.bind(this)
    this.next = this.next.bind(this)
  }
  goBack(){
    this.props.grandParent.setState({
      index: "Start"
    })
  }
  next(){
    this.props.parent.setState({
      index: 1
    })
  }
  render(){
    return(
      <Row>
        <Col>
        <Form>
        <Form.Group controlId="exampleForm">
    <Form.Label>What type of account would you like to open today?</Form.Label>
    <Form.Control as="select" name="accountType" value={this.props.accountType} onChange={this.props.onChange}>
      <option value="checkings">Checkings</option>
      <option value="savings">Savings</option>
      <option value="credit">Credit</option>
    </Form.Control>
  </Form.Group>
    <Button variant="outline-danger" onClick={this.goBack} >Back</Button>
  <Button variant="outline-dark" onClick={this.next} >NEXT</Button>

        </Form>
        </Col>
      </Row>

    )
  }
}



export default SignUp

/*
- Account Type
- name
- Address
- Phone Number
- SSN
- login LoginCredentials

*/
