import React, {Component} from 'react'
import {Container, Row, Col, Table, Button, Form,Image} from 'react-bootstrap'
import Header from '../components/header.js'
import fetch from 'isomorphic-unfetch'

class Records extends Component {

  static async getInitialProps({req}){
    // const id = req.user.User_id
    var res = await fetch(`https://9d170bae.ngrok.io/Transactions/Mixed/14765d72-67c6-11e9-b73a-59c7d9ed16a4/0/secret`)

    const json = await res.json()

    res = await fetch(`https://9d170bae.ngrok.io/Accounts/ids/{id}/secret`)

    const accountsJson = res.json()

    return {transactions: json, ids: accountsJson}
  }

  constructor(props){
    super(props)

    this.state = {
      accounts: [],
      senders: [],
      recievers: [],
      transactions: props.transactions,
      ids: this.props.ids
    }

  }

  // filterAccounts(event){
  //   let account = this.state.account
  //   let transactions = this.state.transactions
  //   let filteredResults = transactions.filter(transaction )
  //
  // }

  filterBySenderRecievers(event){

  }



  render(){
    return(
      <Container>
      <Header />
        <Row>
          <Col>
            <RecordsFilter selectedAccount={this.props.id}/>
          </Col>
          <Col>
            <TransactionsTable transactions={this.state.transactions} ids={this.props.ids} />
          </Col>
        </Row>
      </Container>
    )
  }
}

class RecordsFilter extends Component {
  render(){
    return(
      <Row>
        <Col>
        <p>Filter By:</p>
        <Form>
        <Form.Group>
          <Form.Label>Select an Account:</Form.Label>
          <Form.Control as="select">
            <option>All</option>
          </Form.Control>
        </Form.Group>
        <Form.Group>
          <Form.Label>Sender:</Form.Label>

        </Form.Group>
        <Form.Group>
          <Form.Label>Reciever:</Form.Label>

        </Form.Group>

        </Form>
        </Col>
      </Row>
    )
  }
}

class TableHeader extends Component {
  render(){
    return(
      <th>
        <Button>
        <p>{this.props.title}</p>
        <Image />
        </Button>
      </th>
    )
  }
}

class TableRow extends Component {
  //amount, sender_account_id, receiver_account_id, date_time,sender_name,receiver_name

  render(){
    console.log("idsss")
    console.log(this.props.ids)
    return(
      <tr>
        <td>{this.props.transaction.date_time} </td>
        <td>${this.props.transaction.amount} </td>
        <td>{this.props.ids.includes(this.props.transaction.sender_account_id) ?
          this.props.transaction.sender_account_id :
          this.props.transaction.receiver_account_id } </td>
        <td>{this.props.transaction.sender_name} </td>
        <td>{this.props.transaction.receiver_name} </td>
      </tr>
    )
  }
}

class TransactionsTable extends Component {
  constructor(props){
    super(props)

    this.state = {
      data: this.props.transactions.map((transaction) => <TableRow transaction={transaction} ids={this.props.ids} />)
    }
  }

  render(){
    return(
      <Table striped bordered hover >
        <thead>
          <tr>
            <TableHeader title="Date" />
            <TableHeader title="Amount" />
            <TableHeader title="Account" />
            <TableHeader title="Sender" />
            <TableHeader title="Receiver" />
          </tr>
        </thead>
        <tbody>
          {this.state.data}
        </tbody>
      </Table>
    )
  }
}
